VertexShaderCode:
#version 330 core
layout(location = 0) in vec2 inPosition;
layout(location = 1) in vec4 inColor;
out vec4 fragColor;
void main()
{
    gl_Position = vec4(inPosition, 0.0, 1.0);
    fragColor = inColor;
}

FragmentShaderCode:
#version 330 core
in vec4 fragColor;
out vec4 fragColorOut;
void main()
{
    fragColorOut = fragColor;
}
