VertexShaderCode:
#version 430 core
layout(location = 0) in vec2 inPosition;
out vec4 fragColor;
layout(std140, binding = 1) uniform ColorBufferType
{
    vec4 color;
} colorBuffer;
void main()
{
    gl_Position = vec4(inPosition, 0.0, 1.0);
    fragColor = colorBuffer.color;
}

FragmentShaderCode:
#version 430 core
in vec4 fragColor;
out vec4 fragColorOut;
void main()
{
    fragColorOut = fragColor;
}
