#version 430 core

layout(std140) uniform AttributeColorBuffer{vec2 rg; vec3 padding; vec2 ba;}colorBuffer;

layout(location = 0) out vec4 pixelColor;

void main()
{
	pixelColor = vec4(colorBuffer.rg.x, colorBuffer.rg.y, colorBuffer.ba.x, colorBuffer.ba.y);
}
